export default class BracketTemplateEngine{constructor(r){this.template=r}parseVariables(r){return this.template.replace(/\{\{\s*([\w\s]+)\s*\}\}/g,((e,t)=>{const a=t.trim();if(r.hasOwnProperty(a))return r[a];throw new Error(`Variable '${a}' not found in the template.`)}))}render(r){try{return this.parseVariables(r)}catch(r){throw new Error(`Error rendering template: ${r.message}`)}}}
